class Solution {
    public String multiply(String num1, String num2) {
        //anything multiplied by zero is zero
        if(num1.equals("0") || num2.equals("0")) return "0";
        //if we multiply x * y where xL is number of digits x has and yL is number of digits that y has, the result will have max of xL + yL digits
        int[] digits = new int[num1.length() + num2.length()];

        for(int i = num1.length() - 1; i >= 0; i--){
            int n1 = num1.charAt(i) -'0';
            for(int j = num2.length() - 1; j >= 0; j--){
                int n2 = num2.charAt(j) - '0';
                digits[i + j + 1] += n1 * n2;
            }
        }

        //fix carry over
        int carryOver = 0;
        for(int i = digits.length - 1; i >= 0; i--){
            int curValue = digits[i] + carryOver;            
            if(curValue >= 10){
                carryOver = curValue / 10;
                digits[i] = curValue % 10;
            }            
            else{
                digits[i] = curValue;
                carryOver = 0;
            }
        }

        //turn array into string, first find first non zero index to avoid leading zeros at the begining of string
        int firstNonZeroIdx = 0;
        while(digits[firstNonZeroIdx] == 0) firstNonZeroIdx++;

        StringBuilder sb = new StringBuilder();
        for(int i = firstNonZeroIdx; i < digits.length; i++) sb.append(digits[i]);

        return sb.toString();
    }
}
