class Solution {
    public String multiply(String num1, String num2) {
        int l1 = num1.length();
        int l2 = num2.length();
        int[] multiply = new int[l1+l2];
        int previousCarry = 0;
        int j=0;
        for(int i=l1-1; i>=0; i--) {
            for(j=l2-1; j>=0; j--) {
                // Multiplication of current 2 digits
                int mul = (num1.charAt(i) - '0') * (num2.charAt(j) - '0');

                int temp = mul%10;

                // Get the carry to forward to next multiplication
                int currentCarry = mul/10;

                // Add to current sum at i+j+1 with temp + previous value at [i+j+1] + any previous carry
                int latestValue = multiply[i+j+1] + temp + previousCarry;

                // If this value is greater than 10
                multiply[i+j+1] = latestValue % 10;

                // Add it to currentCarry to add in the next iteration
                currentCarry +=  (latestValue/10);
                previousCarry = currentCarry;
            }

            // Front most digit
            multiply[i+j+1] += previousCarry;
            previousCarry = 0;

        }
        StringBuilder sb = new StringBuilder();

        for(int i=0; i<multiply.length; i++) {
            if(!(sb.length() == 0 && multiply[i] == 0 )) sb.append(multiply[i]);
        }
        return (sb.toString().length() == 0 ? "0" : sb.toString());
    }
}

