            int mul = (num1[i]-'0') * (num2[j]-'0');

            int p1 = i + j, p2 = i + j + 1;

            int sum = mul + res[p2];
            res[p2] = sum % 10;
            res[p1] += sum / 10;
        }

    int i = 0;
    while (i < res.size() && res[i] == 0)
        i++;

    string str;
    for (; i < res.size(); i++)
        str.push_back('0' + res[i]);

    return str.size() == 0 ? "0" : str;
}
};
