public void reorderList(ListNode head) {
	List<ListNode> l = new ArrayList<>();
	for(ListNode iter = head; iter!=null; iter = iter.next)
		l.add(iter);
	int lo = 0, hi = l.size()-1;
	ListNode dummy = new ListNode(-1);
	while(lo <= hi) {
		dummy.next = l.get(lo++);
		dummy = dummy.next;
		dummy.next = l.get(hi--);
		dummy = dummy.next;
	}
	dummy.next = null;                      // eliminate the cycle
}
