class Solution {
public:
    void reorderList(ListNode* head) {
        if(!head) return;

        // Cut the list in half
        ListNode* head2 = cutInHalf(head);

        // Reverse the second part of the list
        head2 = reverse(head2);

        // Merge two list
        merge(head, head2);
    }

    ListNode* cutInHalf(ListNode* head) {
        ListNode* slow = head;
        ListNode* fast = head;

        while(fast->next && fast->next->next) {
            slow = slow->next;
            fast = fast->next->next;
        }

        // Cut in half
        ListNode* mid = slow->next;
        slow->next = NULL;

        return mid;
    }

    ListNode* reverse(ListNode* head) {
        if(!head) return NULL;

        ListNode* curr = head;
        ListNode* temp = head;
        ListNode* prev = NULL;

        while(curr) {
            temp = curr->next;
            curr->next = prev;
            prev = curr;
            curr = temp;
        }
        return prev;
    }


    void merge(ListNode* p, ListNode* q) {
        if(!p) return;
        if(!q) return;

        while(p && q) {
            ListNode* pTemp = p->next;
            ListNode* qTemp = q->next;
            p->next = q;
            p = pTemp;
            q->next = p;
            q = qTemp;
        }
    }
};
