            curr -> next = prev;
            prev = curr;
            curr = forward;
        }
        return prev;
    }
    void reorderList(ListNode* head) {
        if(head == NULL || head -> next == NULL)return;
        // find the mid of the linked list
        ListNode* mid = getmid(head); 
        ListNode* l1 = head;
        ListNode* l2 = (mid -> next);
        mid -> next = NULL;
        l2 = reverse(l2);

        while( l2 != NULL)
        {
            ListNode* f1 =l1 -> next;
        ListNode* f2 = l2 -> next;
            l1 -> next = l2;
            l2 -> next = f1;
            l1 = f1;
            l2 = f2;

        }
    }
};

