            h2 = reverse(slow);
            slow_prev->next = NULL;
        }
        else{
            h2 = reverse(slow->next);
            slow->next = NULL;
        }

        // Traverse both the LL while linking heads of both LL
        ListNode *next1 = h1->next, *next2 = h2->next;
        while(h1 != NULL && h2 != NULL){
            next1 = h1->next;
            next2 = h2->next;

            h1->next = h2;
            h2->next = next1;

            h1 = next1;
