class Solution {
public:
    void reorderList(ListNode* head) {
        // base case
        if(head->next == NULL || head->next->next == NULL) 
            return ;

        // Step 1 - Find middle of the LL with slow-fast pointer approach
        ListNode* slow= head,  *fast= head;
        stack<ListNode*> st;
        while(fast!=NULL && fast->next!=NULL){
            slow= slow->next;
            fast = fast->next->next;
        }

        // Step 2 - Split the LL into 2 parts from the middle
        ListNode* h2;
        if(fast != NULL)
            h2 = slow->next;    
        else 
            h2 = slow;

        // Step 3 - Push the second LL into a stack so that the last element remains at top
        while(h2!=NULL){
            st.push(h2);
            h2 = h2->next;  
        }
        fast = head;

        // Step 4 - Link the first node with the last node and then pop the stack
        while(st.size()){ 
            h2 = fast->next; 
            fast->next = st.top();
            st.top()->next = h2;
            fast = h2;
            st.pop();    
        }

        slow->next = NULL;
    }
};

