        var node = head
        prev = node
        while node != nil && rev != nil {
            prev = node
            next = node!.next
            node!.next = rev
            node = next

            next = rev!.next
            if node != nil { rev!.next = node } /* Note: The "reverse" may be 1 link longer. */
            rev = next
        }
    }
}
