    prev = secondHalf;
    secondHalf = temp;
  }


  let left = head;
  let right = prev;

  // alternate nodes
  while(left && right){
    const tempLeft = left.next;
    const tempRight = right.next;

    left.next = right;
    right.next = tempLeft;

    left = tempLeft;
    right = tempRight;
  }
