            temp1=temp1->next;

            prev->next=head;
            prev=temp2;
            temp2=temp2->next;
        }
        if(head->next==NULL)
        {
            head->next=prev;
        }
        else
        {
            head->next=prev;
            prev->next=temp1;
        }

        }

    }
};

