class Solution {
public:

    void listTraverse(ListNode* head, ListNode*& thead)
    {
        if(head->next)
            listTraverse(head->next,thead);

        if(thead)
        {
            if(thead==head || thead->next==head)
            {
                head->next = NULL;
                thead = NULL;
            }
            else 
            {
                ListNode * tnext = thead->next;
                thead->next = head;
                head->next = tnext;
                thead = tnext;                
