class Solution {
public:

    void reorderList(ListNode* head) {

        // no update needed for 0 or 1 elements
        if(!head || !head->next) return;

        int n = 0;
        ListNode * node = head;
        ListNode * tnode = NULL;
        stack<ListNode *>stak;

        // add all nodes to stack and count total nodes n
        while(node!=NULL)
        {
            stak.push(node);
            node = node->next;
            n++;
        }

        // loop through stack and pop item from top n/2 times
        // add item to the next of current node and set current->next as item->next
        node = head;
        for(int i=0;i<=n/2;i++)
        {
            tnode = stak.top();
            stak.pop();
            tnode->next = node->next;
            node->next = tnode;
            node = tnode->next;
        }

        // set last node's next as NULL to terminate list
        node->next = NULL;

    }
};
```

