class Solution {
public:
    void reorderList(ListNode* head) {

        vector<ListNode*> aList;

        ListNode *aNode = head;
        ListNode *aNext;
        while (aNode) {
            aNext = aNode->next;
            aNode->next = NULL;
            aList.push_back(aNode);
            aNode = aNext;
        }

        int aLo = 1;
        int aHi = aList.size() - 1;

        aNode = head;

        while (aLo <= aHi) {
            aNode->next = aList[aHi--];
            aNode = aNode->next;
            if (aLo <= aHi) {
                aNode->next = aList[aLo++];
                aNode = aNode->next;
            }    
        }
    }
};
