class Solution {
public:
    void reorderList(ListNode* head) {
        vector<int> v;
        ListNode *temp = head;
		// adding values in the vector
        while(temp != NULL) {
            v.push_back(temp->val);
            temp = temp->next;
        }
		// creating two pointers to the vector and iterating accordingly
		// updating the values of the list.
        int start = 0;
        int end = v.size() - 1;
        while(start < end) {
			// for the odd length case
            if(start + 2 == end) {
                head->val = v[start];
                head->next->val = v[end--];
                head->next->next->val = v[start + 1];
                break;
            }
            head->val = v[start++];
            head->next->val = v[end--];
            head = head->next->next;
        }
    }
};
