class Solution {
public:
    void reorderList(ListNode* head) {
        if(!head or !head->next) return;            // if there is no or one node in linked list, no need to reorder

        ListNode *slow=head;
        ListNode *fast=head;
        while(fast->next!=NULL and fast->next->next!=NULL){ // slow and fast pointer approach to find mid/2+1
                                                            // it is setting slow->next to mid/2+1 
            slow=slow->next;                                // in both the cases whether linked list has odd or even nodes 
            fast=fast->next->next;
        }

        ListNode *a=NULL, *b=slow->next;                        
        slow->next=NULL;
        while(b!=NULL){             // reversing second part of linked list
            ListNode *c=b->next;
            b->next=a;
            a=b;
            b=c;
        }

        b=a;                                                    
        a=head;

        while(a!=NULL and b!=NULL){             // mergeing both the linked lists and hence required reordering takes place
            ListNode *t1=a->next;
            ListNode *t2=b->next;
            a->next=b;
            a=t1;
            b->next=a;
            b=t2;
        }
    }
};
