		// To get the size of linked list
        ListNode* temp = head;
        int count = 0;
        while(temp != NULL){
            temp = temp->next;
            count++;
        }

		// To get middle node
        ListNode* slow = head;
        ListNode* fast = head;
        while(fast->next != NULL && fast->next->next != NULL){
            fast = fast->next->next;
            slow = slow->next;
        }

		// Pushing the nodes after middle node into the stack
        slow = slow->next;
        stack<ListNode*> st;
        while(slow != NULL){
            st.push(slow);
            slow = slow->next;
        }

		// connecting the links as required
        temp = head;
        int k = count/2;
        while(k--){
            ListNode* ntem = temp->next;
            ListNode* val = st.top();
            st.pop();
            temp->next = val;
            val->next = ntem;
            temp = ntem;
        }

        temp->next = NULL;

