    void reorderList(ListNode* head) {

        //step 1 - using slow and fast pointer approach to find the mid of the list
        ListNode* slow=head;
        ListNode* fast=head->next;

        while(fast and fast->next)
        {
            slow=slow->next;
            fast=fast->next->next;
