/**
 * Example:
 * var li = ListNode(5)
 * var v = li.`val`
 * Definition for singly-linked list.
 * class ListNode(var `val`: Int) {
 *     var next: ListNode? = null
 * }
 */
class Solution {
    fun reorderList(head: ListNode?): Unit {

        if (head == null || head.next == null) return

        var fast = head; var slow = head
        while(fast?.next != null){
            fast = fast.next.next
            slow = slow?.next
        }

        var _2ndHalfReversed = reverse(slow)
        var _1stHalf = head

        while(_2ndHalfReversed != null && _1stHalf != null){
            var _1stHalfNext = _1stHalf?.next
            var _2ndHalfReversedNext = _2ndHalfReversed?.next
            _1stHalf?.next = _2ndHalfReversed
            _2ndHalfReversed?.next = _1stHalfNext
            _1stHalf = _1stHalfNext
            _2ndHalfReversed = _2ndHalfReversedNext
        }
        if (_2ndHalfReversed != null) _2ndHalfReversed.next = null
        else _1stHalf?.next = null

    }

    fun reverse(head: ListNode?) : ListNode?{

        var curr = head
        var next = curr?.next
        var prev : ListNode? = null

        while(curr != null){
            next = curr.next
            curr.next = prev
            prev = curr
            curr = next            
        }
        return prev
    }
}
