class Solution {
public:

    ListNode* getMiddle(ListNode* head){
        ListNode* slow = head;
        ListNode* fast = head;

        while(fast){
            if(!fast->next or !fast->next->next) return slow;
            fast = fast->next->next;
            slow = slow->next;
        }

        return slow;
    }

    ListNode* reverseList(ListNode* head){
        if(!head) return head;

        ListNode* prev = NULL;
        ListNode* cur = head;
        ListNode* nex = cur->next;

        while(cur){
            cur->next = prev;
            prev = cur;
            cur = nex;
            if(nex) nex = nex->next;
        }

        return prev;
    }

    void reorderList(ListNode* head) {
        ListNode* mid = getMiddle(head);
        ListNode* nextPart = mid->next;
        mid->next = NULL;

        //two pointer:
        ListNode* l1 = head;
        ListNode* l2 = reverseList(nextPart);

        ListNode* result = new ListNode(-1);
        ListNode* res = result;

        while(l1 or l2){
            if(l1) {
                res->next = l1;
                res = res->next;
                l1 = l1->next;
            }
            if(l2){
                res->next = l2;
                res = res->next;
                l2 = l2->next;
            }
        }

        res->next = NULL;
        result->next = NULL;
        return;
    }
};
