 ListNode* swapList(ListNode* head) {
    //Each time call swapList will set L(n-i) = Li->next
    if (head == NULL || head->next == NULL || head->next->next == NULL) 
        return head;

    ListNode* cur = head;
    ListNode* tmp = head->next;
    ListNode* prev = head->next;
    while (tmp->next != NULL) {
        prev = tmp;
        tmp = tmp->next;
    }
    prev->next = NULL;
    tmp->next = cur->next;
    cur->next = tmp;
    cur = cur->next->next;
    swapList(cur);
    return head;
}

void reorderList(ListNode* head) {
    swapList(head);
   }
