    let lastMin=-1;
    let lastMax=-1;
    let start=0;
    let count=0;
    for(let i=0; i<nums.length; i++){
        if(nums[i]<minK || nums[i]>maxK){
            start = i+1;
            lastMin = i;
            lastMax = i;
            continue;
        }
        if(nums[i] == minK){
