typedef long long ll; 

class Solution {
public:
    long long countSubarrays(vector<int>& a, int lk, int rk) {
        ll n = a.size(); 
        ll ans = 0;

        //find position of bad indicies 
        vector<int> bads(1, -1); 

        for (int i = 0; i < n; ++i) {
            p[i+1] += p[i];
            if (lk > a[i] || rk < a[i]) {
                p[i]++; 
                bads.push_back(i);
            }
        }
        bads.push_back(n); 


        for (int i = 0; i < bads.size()-1; ++i) {
            //the subarray from l to r is good. 
            //interestingly, l > r may occur, but the following loop 
            //will not run, and therefore we will continue
            int l = bads[i]+1, r = bads[i+1]-1; 

            //stores the positions of minK and maxK
            vector<int> mns, mxs; 

            for (int j = l; j <= r; ++j) {
                if (a[j] == lk) {
                    mns.push_back(j); 
                } if (a[j] == rk) {
                    mxs.push_back(j); 
                }
            }

            //if minK or maxK does not exist, we can continue to the next segment
            if (min(mns.size(), mxs.size()) == 0) continue; 

            for (int j = l; j <= r; ++j) {
                //if the minimum index does not lie before j we may continue
                if (mns[0] > j || mxs[0] > j) continue; 

                //lowest index to go to 
                int low = 1e9; 

                //find last minK using binary search
                int lo = 0, hi = mns.size()-1; 
                while (lo < hi) {
                    int mid = lo + (hi - lo + 1)/2;
                    if (mns[mid] <= j) lo = mid;
                    else hi = mid - 1; 
                }
                low = min(low, mns[lo]); 

                //find last maxK using binary search
                lo = 0; hi = mxs.size()-1; 
                while (lo < hi) {
                    int mid = lo + (hi - lo + 1)/2;
                    if (mxs[mid] <= j) lo = mid;
                    else hi = mid - 1; 
                }
                low = min(low, mxs[lo]); 

                //increment the answer by the number of unfixed left positions 
                ans += (low - l + 1); 
            }
        }



        return ans; 

    }
};

