            if(num < minK or num > maxK) {
                minFound = false;
                maxFound = false;
                start = i+1;
            }
            if(num == minK) {
                minFound = true;
                minStart = i;
            }
            if(num == maxK) {
                maxFound = true;
                maxStart = i;
            }
            if(minFound and maxFound) {
                ans += (min(minStart, maxStart) - start + 1);
            }
        }
        return ans;
    }
};
