    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        int j = 0;
        int prevMaxIdx = -1, prevMinIdx = -1;
        int n = nums.size();
        long long res = 0;
        for(int i=0;i<n;++i){
            if(nums[i]>maxK or nums[i]<minK){
                prevMaxIdx = -1;
                prevMinIdx = -1;
                j = i+1;
