class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        long long ans = 0;
        int j = -1; //bhai ye hai starting index subarray ka
        int mini = -1; //bro this is index of minimum number jo minK se match krega
        int maxi = -1; //aur ye wala maximum jo match krega

        //ab chalao loop
        for(int i=0; i< nums.size(); i++) {
            //ab bhai minK se chhota koi hai ya maxK se bda koi hai, to aukaat k bhar hai
