class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        int n=nums.size();
        long long ans=0;
        int countMin=0, countMax=0;
        int lastMinPos=0, lastMaxPos=0;
        int start=0;
        for(int i=0;i<n;i++){
            if(nums[i]<minK||nums[i]>maxK){
                countMax=countMin=0;
                start=i+1;
                continue;
            }
            if(nums[i]==minK){
                lastMinPos=i;
                countMin++;
            }
            if(nums[i]==maxK){
                lastMaxPos=i;
                countMax++;
            }
            if(countMin>0&&countMax>0){
                ans+=min(lastMinPos, lastMaxPos)-start+1;
            }
        }
        return ans;
    }
};
