class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        int n = nums.size();
        bool minFound = false, maxFound = false;
        long long ans = 0;
        int start = 0, minIdx, maxIdx;

        for (int i = 0; i < n; ++i) {
            int num = nums[i];
            if (num < minK || num > maxK) {
                minFound = false;
                maxFound = false;
                start = i + 1;
            }
            if (num == minK) {
                minFound = true;
                minIdx = i;
            }
            if (num == maxK) {
                maxFound = true;
                maxIdx = i;
            }
            if (maxFound && minFound) {
                ans += (min(minIdx, maxIdx) - start + 1);
            }
        }
        return ans;
    }
};
