                if(nums[i]==minK) preMin=i;
                if(nums[i]==maxK) preMax=i;
                if(preMin!=-1 && preMax!=-1){
                    ans+=min(preMin,preMax)-preValid;
                }
            }
            else{
                preValid=i;
                preMin=-1;
                preMax=-1;
            }
        }
        return ans;
    }
};
