        size_t min_start = nums.size(); // use an out-of-range value to stand for the fact that it's invalid
        size_t max_start = nums.size();
        size_t start = 0;

        for (size_t i = 0; i != nums.size(); ++i) {
            if (nums[i] < minK || nums[i] > maxK) {
                start = i+1;
