class Solution {
    fun countSubarrays(nums: IntArray, minK: Int, maxK: Int): Long {
        var leftBound = -1
        var maxIdx = -1
        var minIdx = -1
        var result: Long = 0
        for (i in 0..(nums.size - 1)) {
            val curr = nums[i]
            if (minK > curr || curr > maxK) {
                leftBound = i
                continue
            }
            if (minK == curr) {
                minIdx = i
            }
            if (maxK == curr) {
                maxIdx = i
            }
            result += Math.max(0, Math.min(minIdx, maxIdx) - leftBound)
        }
        return result
    }
}
