class Solution {
long long fun(int h, int l,vector<int>&nums, int mini, int maxi){
        queue<int>mx;
        queue<int>mn;
        for(int c=h;c<=l;c++){
          if(nums[c]==maxi)mx.push(c);
          if(nums[c]==mini)mn.push(c);
        }
        long long rev=0;
        for(int c=h;c<=l;c++){
          while(mx.front()<c and mx.size())mx.pop();
          while(mn.front()<c and mn.size())mn.pop();
          if(mx.size()==0 or mn.size()==0)break;
          int pup=max(mn.front(),mx.front());
          rev+=l-pup;
          rev++;
        }
        return rev;
};
public:
