        int sz=nums.size();
      long long ref=0 ;
      long long cnt=-1;
      for(int c=0;c<sz;c++){
        if(nums[c]<mini or nums[c]>maxi){
          ref+=fun(cnt+1,c-1,nums,mini,maxi);
          cnt=c;
        }
      }
      ref+=fun(cnt+1,sz-1,nums,mini,maxi);
      return ref;
    }
};
