class Solution {
public:
    bool condition(int val,int stv,int c){
        if(c)
            return val > stv;
        else
            return stv > val;
    }
    vector<int> computeMinMax(vector<int> nums, int leftToRight, int c){
        stack<int> st;
        int n = nums.size();
        vector<int> res(n);
        int start,end,steps,i;
        if(leftToRight)
            start = 0,end = n,steps = 1;
        else
            start = n-1,end = -1,steps = -1;
        i = start;
        while(i != end){
            while(!st.empty() && condition(nums[i],nums[st.top()],c))
                res[st.top()] = i - steps, st.pop();
            st.push(i);
            i  += steps;
        }
        while(!st.empty()){
            res[st.top()] = end - steps;
            st.pop();
        }
        return res;
    }
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        long long ans = 0;
        int n = nums.size();
        vector<int> mn = computeMinMax(nums,1,0), mx = computeMinMax(nums,1,1),nxtmn(n+1),nxtmx(n+1);
        nxtmn[n] = n,nxtmx[n] = n;
        for(int i=n-1;i>=0;i--){
            nxtmn[i] = n,nxtmx[i] = n;
            if(nums[i] == minK)
                nxtmn[i] = i;
            if(nums[i] == maxK)
                nxtmx[i] = i;
            nxtmx[i] = min(nxtmx[i],nxtmx[i+1]), nxtmn[i] = min(nxtmn[i],nxtmn[i+1]); 
        }
        int i=0,j;
        while(i < n){
            j = i;
            while((j < n) && (nums[j] < maxK) && (nums[j] > minK))
                j++;
            if(!((j < n) && ((nums[j] == minK)||(nums[j] == maxK)))){
                i=j+1;
                continue;
            }
            if(nxtmn[i] != n && nxtmx[i] != n){
                int left = min(nxtmn[i],nxtmx[i]), right = max(nxtmn[i],nxtmx[i]);
                bool failed = false;
                for(int k=left;k<right;k++)
                    if((nums[k]< minK) || (nums[k] > maxK))
                        failed = true;
                if(failed){
                    i=j+1;
                    continue;
                }
                int exright = min(mn[nxtmn[i]], mx[nxtmx[i]]);
                if(exright >= right)
                    ans += (left - i + 1)*(exright - right + 1); 
            }
            i = j+1;
        }
        return ans;
    }
};
```

