class Solution {
public:
    long long countSubarrays(vector<int>& a, int mink, int maxk) {
        ll n=a.size(),ans=0;
        ll i=0,j=0,mini=INT_MAX,maxi=INT_MIN,maxind=-1,minind=-1;
        while(j<n){
            if(a[j]>maxk || a[j]<mink)
            {
                mini=INT_MAX,maxi=INT_MIN,maxind=-1,minind=-1;
                i=j+1;
            }
            else{
                if(maxi<=a[j]) maxi=a[j],maxind=j;
                if(mini>=a[j]) mini=a[j],minind=j;
                if(maxi==maxk && mini==mink) ans+=(min(maxind-i,minind-i)+1);
            }
            j++;
        }
        return ans;
    }
};
