class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        long long ans = 0;
        int n = nums.size();
        int start = 0, mini = -1, maxi = -1;
        for (int i = 0; i < n; i++) {
            if (nums[i] < minK || nums[i] > maxK) {
                mini = maxi = -1;
                start = i + 1;
            }
            if (nums[i] == minK) {
                mini = i;
            }
            if (nums[i] == maxK) {
                maxi = i;
            }
            if (mini != -1 && maxi != -1) {
                ans += (min(mini, maxi) - start + 1);
            }
        }
        return ans;
    }
};

