class Solution {
public:
    long long countSubarrays(vector<int>& nums, int minK, int maxK) {
        int n = nums.size();
        long long maxi = INT_MIN;
        long long mini = INT_MAX;

        int imax = 0, imin = 0;

        int i = 0;
        long long ans = 0;

        for(int j = 0; j < n; j++){
            if(nums[j]>=maxi) maxi = nums[j], imax = j;
            if(nums[j]<=mini) mini = nums[j], imin = j;

            if(maxi==maxK && mini == minK){
                ans += min(imax,imin) - i +1;
            }

            else if(maxi > maxK || mini < minK){
                mini = INT_MAX;
                maxi = INT_MIN;
                i = j+1;
            }
        }
        return ans;
    }
};
