class Solution {
    fun minCost(nums: IntArray, cost: IntArray): Long {
        val max = Collections.max(nums.toList())
        val arr = IntArray(max + 1)
        for (i in nums.indices) {
            arr[nums[i]] += cost[i]
        }
        val pref = LongArray(arr.size)

        var currAdd = 0L
        var curr = 0L
        for (i in arr.indices) {
            currAdd += arr[i]
            curr += currAdd
            pref[i] = curr
