class Solution:
    def minCost(self, nums: List[int], cost: List[int]) -> int:
        pairs = []
        for i in range(len(nums)):
            pairs.append((nums[i], cost[i]))

        pairs.sort()
        cost_mid = sum(cost) / 2
        cost_tmp = 0

        for num, cost in pairs:
            cost_tmp += cost
            if cost_tmp >= cost_mid:
                i = num
                break

        min_cost = 0
        for num, cost in pairs:
            min_cost += abs(num - i) * cost

        return min_cost
