        long long n=nums.size();
        vector<pair<long long, long long> > vp;
        for(int i=0; i<n; i++){
            vp.push_back({nums[i], cost[i]});
        }
        sort(vp.begin(), vp.end());

        long long frequency=0, tmp=0, i=0, median;
        for(auto &it: vp){
            frequency+=it.second;
        }
        while(i<n and tmp<((frequency+1)/2)){
            tmp+=vp[i].second;
            median=vp[i].first;
            i++;
        }
        return calculate(nums, cost, median);
    }
};
