class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
          long long n = nums.size(),sum=0;
         vector<pair<long long,long long>>mp;
		 //First of all treat cost[i] as a frequencies of nums[i] and store it in to a vector.
        for(int i=0;i<n;i++){
            mp.push_back({nums[i],cost[i]});  
        } 
        sort(mp.begin(),mp.end()); // Sort our vector
        for(auto it:mp){
            sum = sum +   (it.second); // Sum up total frequencies of all elements.
        }
   // Here we can also use binary search to find the middle element but I am using linear search here to find the middle element just because it's simple to use.
       long long mid = sum/2 ,temp=0,ans=0;
