class Solution {
public:
//Function to Calculate the totalcost to Make each element of array equal to median element.

long long findcost(vector<int>& nums, vector<int>& cost,int n,int ele){
    long long totalcost=0;
    for(int i=0;i<n;i++){
        totalcost+= 1L * abs(nums[i] - ele) * cost[i];//1L means long

    }
    return totalcost;
}


long long minCost(vector<int>& nums, vector<int>& cost) {
       int n=nums.size();
       vector<pair<int,int>> answer;
       long long sum=0;

//Creating another Vector of pair which stores {array element} + {cost associated} with it

       for(int i=0;i<n;i++)
       {
           answer.push_back({nums[i],cost[i]});
           sum=sum+cost[i];
       }


       long long total=0;
       sort(answer.begin(),answer.end());
       int i=0;
       long long median=0;

//Loop to calculate the median of array.       
       while((total<=sum/2) && i<n)
       {
           total=total+answer[i].second;
           median=answer[i].first;
           i++;

        }

        return findcost(nums,cost,n,median);
   }
};
