# Approach: Use quick select algorithm to find median.
class Solution:
    def minCost(self, nums: List[int], cost: List[int]) -> int:

        A = AA = list(zip(nums, cost))
        leftV, rightV = min(nums), max(nums)
        sumW, leftW = sum(cost), 0

        while rightV != leftV:
            medV = (leftV + rightV) / 2
            tempLeftW = sum(x[1] for x in AA if x[0]<medV)

            if leftW + tempLeftW >= sumW / 2: 
                AA = [x for x in AA if x[0]<medV]
                rightV = max(AA)[0]
            else:
                AA = [x for x in AA if x[0]>=medV]
                leftV = min(AA)[0]
                leftW += tempLeftW

        return sum(abs(v-leftV)*w for v,w in A)
