class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
        ll prefixSum[100005], suffixSum[100005], minCost, totalCost=0LL;
        ll n= nums.size();
        vector<pair<int,int>> nums_cost;
        for(int i=0; i<n; i++)
        {
            nums_cost.push_back(make_pair(nums[i], cost[i]));
            totalCost+= (ll)(nums[i]-1)*cost[i];
        }
        sort(nums_cost.begin(), nums_cost.end());
        prefixSum[0]= nums_cost[0].second;
        suffixSum[n-1]= nums_cost[n-1].second;
        for(int i=1; i<n;i++)
        {
            prefixSum[i]= prefixSum[i-1] + nums_cost[i].second;
        }
        for(int i=n-2; i>=0;i--)
        {
            suffixSum[i]= suffixSum[i+1] + nums_cost[i].second;
        }
        minCost = totalCost;
        for(int i=2; i<=nums_cost[n-1].first; i++)
        {
            pair temp= {i,0};
           auto index= lower_bound(nums_cost.begin(), nums_cost.end(), temp)- nums_cost.begin();

            if(index<n)
            totalCost = totalCost-suffixSum[index];
            if(index>0)
            totalCost= totalCost + prefixSum[index-1]; 

            minCost= min(minCost, totalCost);

        }
        return minCost;
    }
};
