        sort(vec.begin(),vec.end());

        for(int i=0;i<n;i++){
            elements+=1ll*vec[i].second;
        }

        long long median;
        int i=0;

        while(tot<(elements+1)/2 && i<n){
            tot+=(1ll*vec[i].second);
            median=vec[i].first;
            i++;
        }

        return solve(nums,cost,median);
    }
};
```

