class Solution {
    public long minCost(int[] nums, int[] cost) {
        long ans = Long.MAX_VALUE;
        int min  = 1000001;
        int max = 0;

        // find min and max value in nums
        for(int i : nums){
            min = Math.min(min,i);
            max = Math.max(max,i);
        }

        // search B/W min and max value
        while(min<=max){
            int mid = min+(max-min)/2;
            long costLeft = cost(nums,cost,mid-1);
            long costRight = cost(nums,cost,mid+1);
            long costMid = cost(nums,cost,mid);

            // update ans for minimum cost
            ans = Math.min(costMid,ans);
            if(costMid<costLeft){
                min = mid+1;
            }
            if(costMid<costRight){
                max = mid-1;
            }
        }

        return ans;
    }

    // cost of making every element = k
    long cost(int [] nums, int [] cost , int k){
        long res = 0;
        for(int i=0 ; i<nums.length ; i++ ){
            res+=((long)Math.abs(nums[i]-k))*cost[i];
        }

        return res;
    }
}
