class Solution {
public:
    long minOper(vector<int>& nums, vector<int>& cost, int val){
        long minOP = 0;
        for(int i=0;i<cost.size();i++)
            minOP += abs(nums[i] - val) * (long)cost[i];
        return minOP;
    }
    long long minCost(vector<int>& nums, vector<int>& cost) {
        int n = nums.size(), ll = 1, ul = 1e6, mid = 0;
        long ans = INT_MAX;
        while(ll <= ul){
            mid = (ll + ul) >> 1;
            long y1 = minOper(nums,cost,mid), y2 = minOper(nums,cost,mid+1);
            ans = min(y1,y2);
            if(y1 < y2)
                ul = mid - 1;
            else
                ll = mid + 1;
        }
        return ans;
    }
};
```
## *2nd Approach(Using Weighted Median concept):-*
```
class Solution {
public:
    long long minCost(vector<int>& nums, vector<int>& cost) {
        int n = nums.size();
        long count = 0,minCost = 0;;
        vector<pair<int,int>> vec;
        for(int i=0;i<n;i++){
            count += cost[i];
            vec.push_back({nums[i],cost[i]});
        }
        sort(vec.begin(),vec.end());
        long med = (count+1)/2,x;
        for(auto [val,freq]:vec){
            med -= freq;
            if(med <= 0){
                x = val;
                break;
            }
        }
        for(auto [val,freq]:vec)
            minCost += abs(val - x)*(long)freq;
        return minCost;
    }
};
```

