impl Solution {
    pub fn min_cost(nums: Vec<i32>, cost: Vec<i32>) -> i64 {
        let mut num_cost = nums.into_iter().zip(cost.into_iter()).collect::<Vec<(i32, i32)>>();
        num_cost.sort_by(|a, b| a.0.cmp(&b.0));
        let mut sum = 0;
        let (mut cost_left, mut cost_right) = (num_cost[0].1 as i64, 0);
        for i in 1..num_cost.len() {
            sum += (num_cost[0].0 - num_cost[i].0).abs() as i64 * num_cost[i].1 as i64;
            cost_right += num_cost[i].1 as i64;
        }

        let mut min = sum;

        for i in 1..num_cost.len() {
            let val_diff = (num_cost[i].0 - num_cost[i - 1].0).abs() as i64;
            sum += (cost_left as i64 - cost_right as i64) * val_diff;
            cost_right -= num_cost[i].1 as i64;
            cost_left += num_cost[i].1 as i64;

            min = min.min(sum);
        }

        min
    }
}
