impl Solution {
    pub fn min_cost(nums: Vec<i32>, cost: Vec<i32>) -> i64 {

        if nums.iter().all(|&n| n == nums[0]) {
            return 0;
        }

        let mut ids: Vec<usize> = (0..nums.len()).collect();
        ids.sort_unstable_by_key(|&i| nums[i]);
        let mut sum = vec![0; nums.len()];
        sum[0] = cost[ids[0]] as u64;
        for i in 1..nums.len() {
            sum[i] = sum[i - 1] + cost[ids[i]] as u64;
        }

        let target = (sum[nums.len() - 1] - 1) / 2 + 1;
        let mut l = 0;
        let mut r = nums.len();
        while l < r {
            let mid = l + (r - l) / 2;
            if sum[mid] < target {
                l = mid + 1;
            } else {
                r = mid;
            }
        }
        let point = nums[ids[l]];

        nums
         .into_iter()
         .zip(cost.into_iter())
         .fold(0, |acc, (num, cost)| acc + (num - point).abs() as i64 * cost as i64)
    }
}
