        vector<pair<int,int>> v;
        for(int i=0;i<n;i++){
            v.push_back({nums[i],cost[i]});
        }
        sort(v.begin(),v.end());
        long long int x=0;
        for(int i=0;i<n;i++){
            x=x+(long long int )v[i].second;
        }
        long long int total=0;
        long long int value;
        int i=0;
        while(total<(x+1)/2 && i<n){
